import React from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import styles from "./MealsListItem.module.css";
const MealsListItem = props => {
  console.log(props.menuItemDetails);
  return (
    <Card>
      <div className={styles["cart-item"]}>
        <h2>{props.menuItemName}</h2>
        <span>{props.menuItemDetails}</span>

        <div className={styles.summary}>
          <p className={styles.price}>${props.price.toFixed(2)}</p>
          <span>Amount </span> <input type="number" />
          <button>+ Add</button>
        </div>
      </div>
    </Card>
  );
};

export default MealsListItem;
