import React from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import styles from "./MealsListItem.module.css";
import Input from "../UI/Input";
const MealsListItem = props => {
  console.log(props.menuItemDetails);
  return (
    <Card>
      <div className={styles.summary}>
        <h2>{props.name}</h2>
        <span>{props.description}</span>
        <span className={styles.price}>${props.price.toFixed(2)}</span>
      </div>

      <div>
        <Input label="Amount" type="number" />
        {/* <p className={styles.amount}>Amount </p>
          <input type="number" /> */}
        <Button className={props.className}>+ Add</Button>
      </div>
    </Card>
  );
};

export default MealsListItem;
