import React from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import styles from "./MealsListItem.module.css";
import Input from "../UI/Input";
const MealsListItem = props => {
  console.log(props.menuItemDetails);
  return (
    <Card>
      <div>
        {" "}
        className={styles["cart-item"]}
        <div>
          <h2>{props.name}</h2>
          <span className={styles.summary}>{props.description}</span>
          <span className={styles.price}>${props.price.toFixed(2)}</span>
        </div>
        <Input label="Amount" type="number" className={styles.amount} />
        <Button>+ Add</Button>
      </div>
    </Card>
  );
};

export default MealsListItem;
