import React from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import styles from "./MealsListItem.module.css";
import Input from "../UI/Input";
const MealsListItem = props => {
  return (
    <Card>
      <div className={styles["cart-item"]}>
        <div>
          <h2>{props.name}</h2>
          <span className={styles.summary}>{props.description}</span>
          <span className={styles.price}>${props.price.toFixed(2)}</span>
        </div>
        <div className={styles.actions}>
          <Input label="Amount" type="number" />
        </div>
        <button
          style={{
            font: "inherit",
            fontWeight: "bold",
            fontSize: "1.25rem",
            color: "#8a2b06",
            border: "1px solid #8a2b06",
            width: "3rem",
            textAlign: "center",
            borderRadius: "6px",
            backgroundColor: "transparent",
            cursor: "pointer",
            marginLeft: "1rem",
            margin: "0.25rem",
          }}
        >
          {" "}
          + Add
        </button>
      </div>
    </Card>
  );
};

export default MealsListItem;
